import React, {useState, useEffect} from 'react'
import billCalculator from '../service/service';
import axios from "axios";
import delImg from "../delete.png" ;
import loadingImg from "../loading.png" ;
import paymentImg from "../payment.png" ;
import {Link} from "react-router-dom";
import Groups from './Groups';
export default function Home() {

    const memberList =[]
    const [userName,setUserName] = useState("");
    const [no_need_to_split , set_no_split] =useState(false);
    const [res,getRes] = useState();
    const [showExpenses,setShowExpenses] = useState(true);
    const [members, setMembers] = useState([]);
    const [createForm, setCreateForm] = useState({
        payer: "",
        amount: "",
    });

    const [groupList, setGroupList] = useState(null);
    const [selectedGroup, setSelectedGroup] = useState([]);
    const [usersList,setUsersList] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [expenseInput, setExpenseInput] = useState('');
    const [bills, setBills] = useState([]);
    const handleUserSelect = (user) => {
        setSelectedUser(user);
      };

      const handleAddBill = () => {
        if (selectedUser && expenseInput !== '') {
          const newBill = {
            user: selectedUser,
            expense: parseFloat(expenseInput),
          };
          setBills([...bills, newBill]);
          setSelectedUser(null);
          setExpenseInput('');
        }
      };


    const [isLoading,setIsLoading] = useState(false);
    const updateCreateFormField = (e) => {
        const { name, value } = e.target;
    
        setCreateForm({
          ...createForm,
          [name]: value,
        });
      };

    useEffect(() => {
        fetchUserDetails();
        fetchGroups();
    }, []);

    const fetchUserDetails = async() =>{
        const res = await axios.get('/users/userDetails'); 
        setUserName(res.data.name);
    }

    const createBill = async (e) => {
        setIsLoading(true);
        e.preventDefault();
        console.log(createForm);
        let userId =  createForm.payer;
        let amount = createForm.amount;
        let isNewBill=1;
        for(let bill of selectedGroup.bills){
            if(bill.payer_id===userId){
                isNewBill=0;
                break;
            }
        }
        if(isNewBill){
            const res = await axios.post(`/bills/updateBill/${selectedGroup._id}`, {userId,amount});
        }
        else{ // not present create and add in member
            const payer = createForm.payer.toLowerCase();
            const amount = createForm.amount;
            console.log(payer);
            const res = await axios.post("/bills/addBill", {payer,amount});
        }
        setCreateForm({
          payer: "",
          amount: "",
        });
        //*********/ fetchBills();
        setIsLoading(false);
    };

    const deleteMember = async (e) => {
        setIsLoading(true);
        // Delete the note
        const _id = e;
        const res = await axios.delete(`/bills/deleteBill/${_id}`);
        // Update state
        //********** */ fetchBills();
        setIsLoading(false);
    };

    const handleSplitBills  = () =>{
        set_no_split(false);
        if(members.length){ 
            let expense = 0;
            for(let member of members)expense+=member.amount;
            const billresult=billCalculator(members,expense);
            if(billresult.length === 0){
            set_no_split(true);
            }
            else {
            getRes(billresult);
            }
        }
        setShowExpenses(false);
    }

    const handleReset  =async () =>{
        setIsLoading(true);
        const res = await axios.delete(`/bills/deleteBills/`);
        // Update state
        // ********8fetchBills();
        setIsLoading(false);
    }
    const handleShowExpenses = async () => {
        getRes();
        setShowExpenses(!showExpenses);
        set_no_split(false)
    }


    
      const fetchGroups = async() => {
        try{
            const res = await axios.get('/groups/');
            setGroupList(res.data.groups);
            
        }
        catch(err) {
            console.log('Error fetching groups:', err);
            }
      }
      const handleGroupClick = async(groupId) =>{
        try {
            console.log(groupList); 
            const res = await axios.get(`/groups/group-details/${groupId}`); 
            setSelectedGroup(res.data);
            console.log(res.data.users);
            setUsersList(res.data.users);
            console.log(usersList);
          } catch (err) {
            console.log('Error fetching group details:', err);
          }
      }
   

      return (
<div>
    <button className='log-out' ><Link className="link" to="users/logout">Logout</Link></button>

        <div className='container-group'>
        <h2 className='heading-group'>Group Names:</h2>
        <div className='member-list'>
        <ul className='member-item'>
            {groupList && groupList.map(group => (

            <li className='member-name' key={group._id} onClick={ () => handleGroupClick(group._id)}>
               <div className='group-name'>{group.group_name} </div> 
            </li>
            ))}
        </ul>
        </div>
        </div>
        <div className='container-users'>
            ji

            <ul>
            {usersList && usersList.map((user)=>(
                <li key={user._id}>
                {user.user_name}
                </li>
            ))}
            </ul>
        </div>

    



      
        <div className='container'>
            <h2 className='heading'>{userName} , Add Expense Here :</h2>
            <div className='form-group'>
                <form onSubmit={createBill}>
                {usersList.length ? (
    <select onChange={updateCreateFormField} value={createForm.payer} name="payer" required>
        <option value="">Select User</option>
        {usersList.map((user) => (
                    <option key={user._id} value={user._id}>
                        {user.user_name}
                    </option>
                ))}
            </select>
        ) : (
            <input
                onChange={updateCreateFormField}
                value={createForm.payer}
                name="payer"
                placeholder="Member Name"
                type="text"
                required
            />
        )}
                    <input onChange={updateCreateFormField} value={createForm.amount} name="amount" placeholder='Amount' type='number' required/>
                    <button type='submit'>Add Expense</button>
                </form>
            </div>
            {(members.length>0 && !res) && <div className="member-list">
            <h2>Member List:</h2>
            <ul className='member-item'>
            {members && <b><li><div>Payer:</div>Amount:</li></b>}
                {members.map((member, index) => (
                <li key={index}>
                    <div className='payer' >{member.payer}</div>
                    <div className='set-del'>
                    <div className='amount' >Rs {member.amount}</div>
                    <button className='del' onClick={()=>deleteMember(member._id)}><img className="del-img" src={delImg} /> </button>
                    </div>
                </li>
                ))}
            </ul>
            </div>  
        }
            <div>    
                {showExpenses && <button onClick={handleSplitBills}>Split Bill</button>}
                {!showExpenses && <button onClick={handleShowExpenses}>Show Expenses</button>}
                <button onClick={handleReset}>Reset</button> 
                {isLoading && <img className='processing' src={loadingImg} alt='Loading' />}
            {
                no_need_to_split && <div className='no_split'>
                    Don't Worry About Splitting , It's already done.
                </div>
            }
            {res && <div className="member-list">
            <h2>Amount:</h2>
            <ul className='member-item'>
                {res && <b><li>Payer<div>Amount</div>Reciever</li></b>}
                {res.map((member) => (
                <li  key={member}>
                    <div className='payer'>{member[0]}</div> 
                    <div className='amount'>  Rs {member[2].toFixed(2)} 
                    </div> <div className='reciever'>{member[1]}</div>
                </li>
                ))}
            </ul>
            </div>}
            </div>
            
            { !res && members.length==0 && <img className='payment-img' src={paymentImg}/>}
        </div> 
    </div>
  )
