import React, {useState, useEffect} from 'react'
import billCalculator from '../service/service';
import axios from "axios";
import delImg from "../delete.png" ;
import loadingImg from "../loading.png" ;
import paymentImg from "../payment.png" ;
import {Link} from "react-router-dom";
import Groups from './Groups';
export default function Home() {

    const memberList =[]
    const [userName,setUserName] = useState("");
    const [no_need_to_split , set_no_split] =useState(false);
    const [res,getRes] = useState();
    const [showExpenses,setShowExpenses] = useState(true);
    const [members, setMembers] = useState([]);
    const [createForm, setCreateForm] = useState({
        payer: "",
        amount: "",
    });

    const [groupList, setGroupList] = useState(null);
    const [selectedGroup, setSelectedGroup] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [expenseInput, setExpenseInput] = useState('');
    const [bills, setBills] = useState([]);
    const handleUserSelect = (user) => {
        setSelectedUser(user);
      };

      const handleAddBill = () => {
        if (selectedUser && expenseInput !== '') {
          const newBill = {
            user: selectedUser,
            expense: parseFloat(expenseInput),
          };
          setBills([...bills, newBill]);
          setSelectedUser(null);
          setExpenseInput('');
        }
      };


    const [isLoading,setIsLoading] = useState(false);
    const updateCreateFormField = (e) => {
        const { name, value } = e.target;
    
        setCreateForm({
          ...createForm,
          [name]: value,
        });
      };

    useEffect(() => {
        fetchGroupDetails();
        fetchUserDetails();
        fetchGroups();
    }, []);

    const fetchUserDetails = async() =>{
        const res = await axios.get('/users/userDetails'); 
        setUserName(res.data.name);
    }

    const fetchGroupDetails = async () => {
        try{
            // let token=localStorage.getItem("accesstoken")
            // axios.defaults.headers.common['token'] = token ;
            const res = await axios.get("/bills/getbills");
            // Set to state
            setMembers(res.data.bills);
        }catch(err){
            console.log(err);   
          }
    };
    const handleGroupExpenseSubmit = () => {
        // Logic to split bill among all members
        // Replace bills list with transactions list
      };
      const handleSplitBill = () => {
        // Logic to split bill among all members
        // Replace bills list with transactions list
      };

    const createBill = async (e) => {
        setIsLoading(true);
        e.preventDefault();
        // check present or not in members if yes just do update
        const member =  members.find((member) => member.payer === createForm.payer.toLowerCase());
        if(member){
            console.log(member);
            const payer = member.payer;
            const amount = parseInt(member.amount) + parseInt(createForm.amount);
            member.amount = amount;
            const res = await axios.put(`/bills/updateBill/${member._id}`, {payer:payer,amount:amount});
        }
        else{ // not present create and add in member
            const payer = createForm.payer.toLowerCase();
            const amount = createForm.amount;
            console.log(payer);
            const res = await axios.post("/bills/addBill", {payer,amount});
        }
        setCreateForm({
          payer: "",
          amount: "",
        });
        //*********/ fetchBills();
        setIsLoading(false);
    };

    const deleteMember = async (e) => {
        setIsLoading(true);
        // Delete the note
        const _id = e;
        const res = await axios.delete(`/bills/deleteBill/${_id}`);
        // Update state
        //********** */ fetchBills();
        setIsLoading(false);
    };

    const handleSplitBills  = () =>{
        set_no_split(false);
        if(members.length){ 
            let expense = 0;
            for(let member of members)expense+=member.amount;
            const billresult=billCalculator(members,expense);
            if(billresult.length === 0){
            set_no_split(true);
            }
            else {
            getRes(billresult);
            }
        }
        setShowExpenses(false);
    }

    const handleReset  =async () =>{
        setIsLoading(true);
        const res = await axios.delete(`/bills/deleteBills/`);
        // Update state
        // ********8fetchBills();
        setIsLoading(false);
    }
    const handleShowExpenses = async () => {
        getRes();
        setShowExpenses(!showExpenses);
        set_no_split(false)
    }


    
      const fetchGroups = async() => {
        try{
            const res = await axios.get('/groups/');
            setGroupList(res.data.groups);
        }
        catch(err) {
            console.log('Error fetching groups:', err);
            }
      }
      const handleGroupClick = async(groupId) =>{
        try {
            const res = await axios.get(`/groups/group-details/${groupId}`); 
            // console.log(res.data)
            setSelectedGroup(res.data);
            console.log(selectedGroup)
          } catch (err) {
            console.log('Error fetching group details:', err);
          }
      }

      return (
<div>
        
            <div>
        <h2>Group Names:</h2>
        <ul>
            {groupList && groupList.map(group => (

            <li key={group._id} onClick={ () => handleGroupClick(group._id)}>
                {group.group_name}
                </li>
            ))}
        </ul>
        </div>

         <div className="container">
      <div className="middleScreen">
        <form onSubmit={handleGroupExpenseSubmit}>
                <select value={createForm.payer} name="payer" placeholder='Member Name' type="text" required>
                    {/* { selectedGroup.users.map((member)=>{
                        <option>member.name</option>
                    })
                    } */}
                </select>
                <input onChange={updateCreateFormField} value={createForm.amount} name="amount" placeholder='Amount' type='number' required/>
                <button type='submit'>Add Expense</button>
        </form>

          {/* Box to take input for expense */}
          <div className="expenseBox">
            {/* Input for expense */}
            {/* Button to add bill */}
            <button onClick={handleAddBill}>Add Bill</button>
          </div>
        </div>

        {/* Button to split bill */}
        <button onClick={handleSplitBill}>Split Bill</button>

        {/* List of bills */}
        <div className="billsList">
          {/* Display list of bills */}
          {/* Display giver, amount, delete button */}
        </div>

        {/* List of transactions */}
        {/* Display giver, taker, amount */}
    
    </div> 



        /* <button className='log-out' ><Link className="link" to="users/logout">Logout</Link></button>
        <div className='container'>
            <h2 className='heading'>{userName} , Add Expense Here :</h2>
            <div className='form-group'>
                <form onSubmit={createBill}>
                    <input onChange={updateCreateFormField} value={createForm.payer} name="payer" placeholder='Member Name' type="text" required/>
                    <input onChange={updateCreateFormField} value={createForm.amount} name="amount" placeholder='Amount' type='number' required/>
                    <button type='submit'>Add Expense</button>
                </form>
            </div>
            {(members.length>0 && !res) && <div className="member-list">
